import React, { useState, useEffect } from 'react';
import { Box, Button, Typography, LinearProgress, Paper, Grid } from '@mui/material';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import DownloadIcon from '@mui/icons-material/Download';
import { styled } from '@mui/material/styles';

const Input = styled('input')({
    display: 'none',
});

const FileUpload = () => {
    const [standardFile, setStandardFile] = useState(null);
    const [validationFile, setValidationFile] = useState(null);
    const [uploading, setUploading] = useState(false);
    const [progress, setProgress] = useState(0);
    const [resultFile, setResultFile] = useState(null);
    const [error, setError] = useState(null);
    const [ws, setWs] = useState(null);

    useEffect(() => {
        // 建立WebSocket连接
        const websocket = new WebSocket('ws://localhost:3001');

        websocket.onopen = () => {
            console.log('WebSocket Connected');
        };

        websocket.onmessage = (event) => {
            const data = JSON.parse(event.data);
            if (data.type === 'progress') {
                setProgress(data.progress);
            }
        };

        websocket.onerror = (error) => {
            console.error('WebSocket error:', error);
        };

        setWs(websocket);

        return () => {
            websocket.close();
        };
    }, []);

    const handleStandardFileChange = (event) => {
        const file = event.target.files[0];
        if (file && file.name.endsWith('.csv')) {
            setStandardFile(file);
            setError(null);
        } else {
            setError('标准数据必须是CSV文件');
        }
    };

    const handleValidationFileChange = (event) => {
        const file = event.target.files[0];
        if (file && (file.name.endsWith('.csv') || file.name.endsWith('.pkl'))) {
            setValidationFile(file);
            setError(null);
        } else {
            setError('待验证数据必须是CSV或PKL文件');
        }
    };

    const handleUpload = async () => {
        if (!standardFile || !validationFile) {
            setError('请选择标准数据和待验证数据文件');
            return;
        }

        setUploading(true);
        setProgress(0);
        setError(null);

        const formData = new FormData();
        formData.append('standard', standardFile);
        formData.append('validation', validationFile);

        try {
            const response = await fetch('http://localhost:3001/api/upload', {
                method: 'POST',
                body: formData,
            });

            const data = await response.json();

            if (response.ok) {
                setResultFile(data.resultFile);
            } else {
                setError(data.error || '上传失败');
            }
        } catch (err) {
            setError('上传过程中发生错误');
            console.error('Upload error:', err);
        } finally {
            setUploading(false);
        }
    };

    const handleDownload = () => {
        if (resultFile) {
            window.open(`http://localhost:3001/api/download/${resultFile}`, '_blank');
        }
    };

    return (
        <Box sx={{ maxWidth: 800, mx: 'auto', mt: 4, p: 3 }}>
            <Paper elevation={3} sx={{ p: 4 }}>
                <Typography variant="h4" gutterBottom align="center">
                    医疗数据验证系统
                </Typography>

                <Grid container spacing={3}>
                    <Grid item xs={12}>
                        <Box sx={{ mb: 2 }}>
                            <label htmlFor="standard-file">
                                <Input
                                    accept=".csv"
                                    id="standard-file"
                                    type="file"
                                    onChange={handleStandardFileChange}
                                    disabled={uploading}
                                />
                                <Button
                                    variant="contained"
                                    component="span"
                                    startIcon={<CloudUploadIcon />}
                                    fullWidth
                                    disabled={uploading}
                                >
                                    选择标准数据文件 (CSV)
                                </Button>
                            </label>
                            {standardFile && (
                                <Typography variant="body2" sx={{ mt: 1 }}>
                                    已选择: {standardFile.name}
                                </Typography>
                            )}
                        </Box>
                    </Grid>

                    <Grid item xs={12}>
                        <Box sx={{ mb: 2 }}>
                            <label htmlFor="validation-file">
                                <Input
                                    accept=".csv,.pkl"
                                    id="validation-file"
                                    type="file"
                                    onChange={handleValidationFileChange}
                                    disabled={uploading}
                                />
                                <Button
                                    variant="contained"
                                    component="span"
                                    startIcon={<CloudUploadIcon />}
                                    fullWidth
                                    disabled={uploading}
                                >
                                    选择待验证数据文件 (CSV/PKL)
                                </Button>
                            </label>
                            {validationFile && (
                                <Typography variant="body2" sx={{ mt: 1 }}>
                                    已选择: {validationFile.name}
                                </Typography>
                            )}
                        </Box>
                    </Grid>

                    <Grid item xs={12}>
                        <Button
                            variant="contained"
                            color="primary"
                            onClick={handleUpload}
                            disabled={!standardFile || !validationFile || uploading}
                            fullWidth
                            sx={{ mb: 2 }}
                        >
                            开始验证
                        </Button>
                    </Grid>

                    {uploading && (
                        <Grid item xs={12}>
                            <Box sx={{ width: '100%', mb: 2 }}>
                                <LinearProgress variant="determinate" value={progress} />
                                <Typography variant="body2" color="text.secondary" align="center" sx={{ mt: 1 }}>
                                    {Math.round(progress)}%
                                </Typography>
                            </Box>
                        </Grid>
                    )}

                    {error && (
                        <Grid item xs={12}>
                            <Typography color="error" align="center">
                                {error}
                            </Typography>
                        </Grid>
                    )}

                    {resultFile && (
                        <Grid item xs={12}>
                            <Button
                                variant="contained"
                                color="success"
                                startIcon={<DownloadIcon />}
                                onClick={handleDownload}
                                fullWidth
                            >
                                下载验证结果
                            </Button>
                        </Grid>
                    )}
                </Grid>
            </Paper>
        </Box>
    );
};

export default FileUpload;
